程式碼 #include &lt;bits/stdc++.h&gt; using namespace std; class Node{ public: int start; int end; int max; int min; int sum; Node *left=nullptr; Node *right=nullptr; Node(int start,int end,int max,int min,int sum,Node *left=nullptr,Node *right=nullptr): start(start),end(end),max(max),min(min),sum(sum),left(left),right(right){} }; Node *build(int start,int end,int val[]){ if(start==end){ //cout&lt;&lt;&#34;build: [&#34;&lt;&lt;start&lt;&lt;&#34;, &#34;&lt;&lt;end&lt;&lt;&#34;] node-&gt;,max,min,sum=&#34;&lt;&lt;val[start]&lt;&lt;&#34;
&#34;; return new Node(start,end,val[start],val[start],val[start],nullptr,nullptr); } int mid=(start+end)/2; Node *left=build(start,mid,val); Node *right=build(mid+1,end,val); //cout&lt;&lt;&#34;build: [&#34;&lt;&lt;start&lt;&lt;&#34;, &#34;&lt;&lt;end&lt;&lt;&#34;]&#34;&lt;&lt;&#34; node-&gt;max=&#34;&lt;&lt;max(left-&gt;max,right-&gt;max)&lt;&lt;&#34; node-&gt;min=&#34;&lt;&lt;min(left-&gt;min,right-&gt;min)&lt;&lt;&#34; node-&gt;sum=&#34;&lt;&lt;left-&gt;sum+right-&gt;sum&lt;&lt;&#34;
&#34;; return new Node(start,end,max(left-&gt;max,right-&gt;max),min(left-&gt;min,right-&gt;min),left-&gt;sum+right-&gt;sum,left,right); } int query_sum(Node *root,int i,int j){ int sum=0; //cout&lt;&lt;&#34;query: [&#34;&lt;&lt;i&lt;&lt;&#34;, &#34;&lt;&lt;j&lt;&lt;&#34;] @ [&#34;&lt;&lt;root-&gt;start&lt;&lt;&#34;, &#34;&lt;&lt;root-&gt;end&lt;&lt;&#34;] root-&gt;sum=&#34;&lt;&lt;root-&gt;sum&lt;&lt;&#34;
&#34;; if(root-&gt;start==root-&gt;end) return root-&gt;sum; if(root-&gt;start==i&amp;&amp;root-&gt;end==j) return root-&gt;sum; int mid=root-&gt;start+(root-&gt;end-root-&gt;start)/2; if(j&lt;=mid) return query_sum(root-&gt;left,i,j); else if(i&gt;mid) return query_sum(root-&gt;right,i,j); else return query_sum(root-&gt;left,i,mid)+query_sum(root-&gt;right,mid+1,j); } int query_max(Node *root,int i,int j){ int sum=0; //cout&lt;&lt;&#34;query: [&#34;&lt;&lt;i&lt;&lt;&#34;, &#34;&lt;&lt;j&lt;&lt;&#34;] @ [&#34;&lt;&lt;root-&gt;start&lt;&lt;&#34;, &#34;&lt;&lt;root-&gt;end&lt;&lt;&#34;] root-&gt;max=&#34;&lt;&lt;root-&gt;max&lt;&lt;&#34;
&#34;; if(root-&gt;start==root-&gt;end) return root-&gt;max; if(root-&gt;start==i&amp;&amp;root-&gt;end==j) return root-&gt;max; int mid=root-&gt;start+(root-&gt;end-root-&gt;start)/2; if(j&lt;=mid) return query_max(root-&gt;left,i,j); else if(i&gt;mid) return query_max(root-&gt;right,i,j); else return max(query_max(root-&gt;left,i,mid),query_max(root-&gt;right,mid+1,j)); } int query_min(Node *root,int i,int j){ int sum=0; //cout&lt;&lt;&#34;query: [&#34;&lt;&lt;i&lt;&lt;&#34;, &#34;&lt;&lt;j&lt;&lt;&#34;] @ [&#34;&lt;&lt;root-&gt;start&lt;&lt;&#34;, &#34;&lt;&lt;root-&gt;end&lt;&lt;&#34;] root-&gt;min=&#34;&lt;&lt;root-&gt;min&lt;&lt;&#34;
&#34;; if(root-&gt;start==root-&gt;end) return root-&gt;min; if(root-&gt;start==i&amp;&amp;root-&gt;end==j) return root-&gt;min; int mid=root-&gt;start+(root-&gt;end-root-&gt;start)/2; if(j&lt;=mid) return query_min(root-&gt;left,i,j); else if(i&gt;mid) return query_min(root-&gt;right,i,j); else return min(query_min(root-&gt;left,i,mid),query_min(root-&gt;right,mid+1,j)); }

簡介 Flood Fill 演算法是從一個區域中提取若干個連通的點與其他相鄰區域區分開(或分別染成不同顏色)的經典演算法(Algorithm)。因為其思路類似洪水從一個區域擴散到所有能到達的區域而得名。
實作方法 深度優先搜尋(Depth-First Search,DFS) 廣度優先搜尋(Breadth-First Search,BFS) BFS 實作 把最外面的點加入佇列(Queue)裡面，分別按照佇列中的點染色
